import React, { version,useEffect } from 'react';
import {FlatList,TouchableOpacity,StyleSheet,Image, Text, View, TextInput, TextComponent } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import AsyncStorage from '@react-native-async-storage/async-storage';
export default function OpenList({navigation}) {
    async function save(){
      await AsyncStorage.setItem('@WishList', "");
    } 
    async function read(){
      alert(await AsyncStorage.getItem('@WishList'));
    } 
    const [dataL, setDataL] = React.useState(AsyncStorage.getItem('@WishList'));
    var sdata = new Array;

    sdata.push(JSON.parse(JSON.stringify(dataL)));
    console.log(sdata)
    const ListG = () =>{
        return(
        <FlatList
        data={sdata}
        renderItem={({item}) => 
        <Text>{item._name}</Text>
      }/>

      );

      
    }

    return (
    <View style={styles.container}>
            <ListG/>

              <Image
        style={{height:200,width:200,marginBottom:70,marginTop:20}} source={require('../assets/l.png')}
      />
    <Text>A</Text>
        
      <TouchableOpacity onPress={save}><Text>Touch me if u want to save some shit</Text></TouchableOpacity>
      <TouchableOpacity onPress={read}><Text>Touch me if u want to read some shit</Text></TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
  });
