import React, { version,useEffect, useState } from 'react';
import {FlatList,TouchableOpacity,StyleSheet,Image, Text, View, TextInput, TextComponent, ScrollView } from 'react-native';
import { Ionicons } from '@expo/vector-icons';

export default function ListDetail({route,navigation}) {
  const { itemId } = route.params;
  const [dataapi, setDataapi] = useState([])

  function LoadId(){
    fetch('http://192.168.1.107:45455/api/wishlist/'+itemId).then((response)=>response.json()).then((json)=>LoadData(json.id));
  }
  function LoadData(id:any){
    fetch('http://192.168.1.107:45455/api/items/'+id).then((response)=>response.json()).then((data)=>setDataapi(data))
  }
  LoadId()  ;

    return (
        <View>
          <Text>itemId: {(itemId)}</Text>
          {dataapi.map((aa) => (
          <TouchableOpacity onPress={()=>{navigation.navigate('ListDetail', {itemId:aa._id})}}>
            <View style={styles.item}>
            <View style={{borderLeftWidth:2,borderLeftColor:'#'+aa._color,padding:5}}>
              <Text style={{fontSize:20}}>{(aa.name)}</Text>
            </View>
          </View>
          </TouchableOpacity>
          ))}
        </View>
  );
}

const styles = StyleSheet.create({
    container: {
      alignItems: 'center',
    },
    item:{
      backgroundColor: '#fff',
      padding:10,
      margin:5,
      width:'100%'
    }
  });
