import React, { version,useEffect, useState } from 'react';
import {FlatList,TouchableOpacity,StyleSheet,Image, Text, View, TextInput, TextComponent, ScrollView } from 'react-native';
import { Ionicons } from '@expo/vector-icons';

export default function AddItem({route,navigation}) {
  const { url } = route.params;

    return (
        <View style={styles.container}>
          <Text style={{fontSize:20}}>Add</Text>
          
          <Text>url: {(url)}</Text>


          <View style={styles.container}>
              <Image style={{height:200,width:200,marginBottom:70,marginTop:20}} source={require('../assets/l.png')}/>
    
            <View>
              <TextInput style={{borderBottomWidth:1,height:50,textAlign:'center',margin:20, }} placeholder="Muj uzasny seznam"/>
              <TextInput style={{borderWidth:1,padding:10,borderColor:'grey',borderRadius:20,margin:20,minWidth:'95%',maxWidth:'100%'}} multiline={true} placeholder="Popis meho uzasny seznam"></TextInput>
            </View>

            <TouchableOpacity onPress={()=>{alert('a')}} style={styles.button}>
              <Ionicons name="create-outline" color="white" size={25}></Ionicons>
      
              <Text style={styles.text}>Vytvo≈ôit seznam</Text>
            </TouchableOpacity>
            <Text style={{color:'red',fontWeight:'100',opacity:0.7}}>Ahoj</Text>
          </View>
          
        </View>
  );
}

const styles = StyleSheet.create({
  button:{
  display:'flex',
  margin:5,
  padding:6,
  flexDirection:'row',
  alignItems:'center',
  backgroundColor:'#FF6D6D',
  justifyContent:'center',
  borderRadius:20,
},
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  text:{
    fontSize:15,
    padding:8,
    fontWeight:'100',
    color:'white'
  }
});
