import React, { version,useEffect, useState } from 'react';
import {FlatList,TouchableOpacity,StyleSheet,Image, Text, View, TextInput, TextComponent, ScrollView } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import AsyncStorage from '@react-native-async-storage/async-storage';
export default function OpenList({navigation}) {

  const [data, setData] = useState([])



  useEffect(() => {
    const fetchpairs = async() => {
      var raw = await AsyncStorage.getItem('@WishList');
      setData(JSON.parse(raw+""))
    }
    fetchpairs()
}, [])



    async function save(){
      await AsyncStorage.setItem('@WishList', "");
    } 
    async function read(){
      alert(await AsyncStorage.getItem('@WishList'));
    } 

    const ListG = () =>{
        return(
          <ScrollView>
          {[data].map((data) => (<Text>{JSON.stringify(data)}</Text>))}
      </ScrollView>
      );

      
    }

    return (
    <View style={styles.container}>
            <ListG/>

              <Image
        style={{height:200,width:200,marginBottom:70,marginTop:20}} source={require('../assets/l.png')}
      />
    <Text>A</Text>
        
      <TouchableOpacity onPress={save}><Text>Touch me if u want to save some shit</Text></TouchableOpacity>
      <TouchableOpacity onPress={read}><Text>Touch me if u want to read some shit</Text></TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
  });
