import React, { useState, useEffect } from 'react';
import { Text, View, StyleSheet, Button } from 'react-native';
import { BarCodeScanner } from 'expo-barcode-scanner';
import AsyncStorage from '@react-native-async-storage/async-storage';


export default function App({navigation}) {
  const [hasPermission, setHasPermission] = useState(null);
  const [scanned, setScanned] = useState(false);

  async function SaveQr(params:any) {
    await fetch('http://192.168.1.107:45455/api/wishlist/'+params).then((res)=>res.json()).then((json=>save(json)));
  }

  async function save(data:any){
    var raw = await AsyncStorage.getItem('@WishList');
    if(raw){
      var send = new Array();
      var old = JSON.parse(raw)
      send.push(data);
      console.log(send);
      await AsyncStorage.setItem('@WishList',JSON.stringify(old.concat(send)));
    }
    else{
      var send = new Array();
      send.push(data);
      await AsyncStorage.setItem('@WishList',JSON.stringify(send) );
    }
  } 


  useEffect(() => {
    (async () => {
      const { status } = await BarCodeScanner.requestPermissionsAsync();
      setHasPermission(status === 'granted');
    })();
  }, []);

  const handleBarCodeScanned = ({data }) => {
    setScanned(true);
    alert(data)
    navigation.navigate('Open')
  };

  if (hasPermission === null) {
    return <Text>Requesting for camera permission</Text>;
  }
  if (hasPermission === false) {
    return <Text>No access to camera</Text>;
  }

  return (
    <View style={styles.container}>
      <BarCodeScanner
        onBarCodeScanned={scanned ? undefined : handleBarCodeScanned}
        style={StyleSheet.absoluteFillObject}
      />
      {scanned && <Button title={'Tap to Scan Again'} onPress={() => setScanned(false)} />}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'center',
  },
});
