import React, { version } from 'react';
import {FlatList,TouchableOpacity,StyleSheet,Image, Text, View, TextInput, TextComponent } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import AsyncStorage from '@react-native-async-storage/async-storage';
export default async function OpenList({navigation}) {
    async function save(){
      await AsyncStorage.setItem('@WishList', "");
    } 
    async function read(){
      alert(await AsyncStorage.getItem('@WishList'));
    } 
    var dataL = await AsyncStorage.getItem('@WishList');
    

    if (dataL) {
      return (
        <View style={styles.container}>
    
                  <Image
            style={{height:200,width:200,marginBottom:70,marginTop:20}} source={require('../assets/l.png')}
          />
        <Text>A</Text>
            <FlatList
              data={JSON.parse(dataL)}
              renderItem={({item}) => 
              <Text>{item._name}</Text>
            }/>
          <TouchableOpacity onPress={save}><Text>Touch me if u want to save some shit</Text></TouchableOpacity>
          <TouchableOpacity onPress={read}><Text>Touch me if u want to read some shit</Text></TouchableOpacity>
        </View>
      );
    }
    else{
      return(
        <View><Text>PeepoSad</Text></View>
      )
    }
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
  });
